    private void btBuku1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btBuku1ActionPerformed
        tfJudul.setText("Bumi");
        tfPenerbit.setText("Gramedia Pustaka Utama");
        tfTglTerbit.setText("16 Januari 2014");
        tfPenulis.setText("Tere Liye");      
        tfDeskripsi.setText("Novel Bumi mengisahkan seorang remaja \nperempuan bernama Raib. Dikisahkan \nbahwa Raib bisa bertemu sosok kurus \ntinggi bernama Tamus. Makhluk kurus dan \ntinggi itu muncul di dalam cermin \nkamar Raib.");        
    }//GEN-LAST:event_btBuku1ActionPerformed

