    private void btBuku2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btBuku2ActionPerformed
        tfJudul.setText("Kata");
        tfPenerbit.setText("Gagas Media");
        tfTglTerbit.setText("07 November 2018");
        tfPenulis.setText("Rintik Sedu");      
        tfDeskripsi.setText("Novel Kata oleh Rintik Sedu bercerita \ntentang hati Binta yang penuh bimbang. \nBinta terjebak dalam masa lalu yang \nbelum usai, sementara cinta baru yang \nhangat sudah siap menyambut dirinya \nuntuk melangkah maju. ");
    }//GEN-LAST:event_btBuku2ActionPerformed

    private void btBuku3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btBuku3ActionPerformed
        tfJudul.setText("Sang Pemimpi");
        tfPenerbit.setText("Benteng Pustaka");
        tfTglTerbit.setText("Juli 2006");
        tfPenulis.setText("Andrea Hirata");
        tfDeskripsi.setText("Dalam novel ini, Andrea mengeksplorasi \nhubungan persahabatannya dengan dua anak \nyatim piatu, Arai Ichsanul Mahidin \ndan Jimbron, serta kekuatan mimpi yang \ndapat membawa Andrea dan Arai melanjutkan \nstudi ke Sorbonne, Paris, Prancis.");
    }//GEN-LAST:event_btBuku3ActionPerformed

